import './App.css';
import titleimg from './Title.png'
import React from 'react';
import { Container, InputAdornment, TextField } from "@mui/material";
import { useState, useEffect } from "react";
import SearchIcon from "@mui/icons-material/Search";
import Button from '@mui/material/Button';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'


function App() {
  const baseURL = 'http://127.0.0.1:8000/search/';
  const [searchTerm, setSearchTerm] = useState("");
  const [picId, setPicId] = React.useState("");

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };
  const searchWord = searchTerm.split(' ').join('_')
  const getPics = () => {
    axios.get(baseURL + searchWord)
      .then((response) => {
        setPicId(response.data);
        console.log(picId)
        console.log('get corresponding pictures with key word: ' + searchWord)
      })
      .catch(err => {
        console.log(err)

      })
  }
  const routeChange = () => {
    getPics();
    let path = `diaplay`;
    navigate(path);
  }
  return (
    <div className="App">
      <img src={titleimg} style={{ marginTop: 370 }} />
      <Container maxWidth="md" sx={{ mt: 20 }}>
        <TextField
          id="search"
          type="search"
          InputLabelProps={{ style: { color: '#CB8A7C' } }}
          label="Please Enter Keywords for Image Search"
          value={searchTerm}
          onChange={handleChange}
          sx={{ width: 600, borderColor: '#CB8A7C' }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <SearchIcon />
              </InputAdornment>
            )
          }}
        />
        <Button onClick={() => { routeChange(); }} sx={{
          background: '#CB8A7C', ':hover': {
            bgcolor: '#E2745D',
            color: 'white',
          },
        }} variant="contained" size="large" style={{ marginLeft: 20, marginTop: 5 }}>Search</Button>
      </Container>
    </div>

  );
}

export default App;
